(()=>{const e=document.querySelector(".add"),t=document.querySelector(".search input"),r=document.querySelector(".todos");e.addEventListener("submit",(t=>{t.preventDefault();const n=e.add.value.trim();n.length&&((e=>{const t=`\n    <li class="list-group-item d-flex justify-content-between align-items-center">\n      <span>${e}</span>\n      <i class="far fa-trash-alt delete"></i>\n    </li>\n  `;r.innerHTML+=t})(n),e.reset())})),r.addEventListener("click",(e=>{e.target.classList.contains("delete")&&e.target.parentElement.remove()})),t.addEventListener("keyup",(()=>{var e;e=t.value.trim().toLowerCase(),Array.from(r.children).filter((t=>!t.textContent.toLowerCase().includes(e))).forEach((e=>e.classList.add("filtered"))),Array.from(r.children).filter((t=>t.textContent.toLowerCase().includes(e))).forEach((e=>e.classList.remove("filtered")))}))})();